$.extend($.expr[':'], {
    'containsi': function(elem, i, match, array) {
        return (elem.textContent || elem.innerText || '').toLowerCase()
            .indexOf((match[3] || "").toLowerCase()) >= 0;
    }
});

$(document).ready(function(e) {
    $("form[ajax=true]").submit(function(e) {
        e.preventDefault(); //disativa azioni di default del bottone
        var form_data = $(this).serialize(); //crea un json con i dati della form
        var form_url = $(this).attr("action"); //legge il parametro action
        var form_method = $(this).attr("method").toUpperCase(); //legge il parametro methos
        $.ajax({
            url: form_url,
            type: form_method,
            data: form_data,
            cache: false,
            success: function(returnhtml) {
                $("#result").html(returnhtml);
            }
        });
    });

    $('.list-count').text($('#list .in').length + ' items');
    $("#search-text").keyup(function () {  
        var searchTerm = $("#search-text").val();
        var searchSplit = ":containsi('" + searchTerm.replace(/ /g, "'):containsi('") + "')";

        $("#list li").not(searchSplit).addClass('hidden out').removeClass('in');
        $("#list li" + searchSplit).removeClass('hidden out').addClass('in');

        var jobCount = $('#list .in').length;
        $('.list-count').text(jobCount + ' items');

        if(jobCount === 0) {
            $('#list').addClass('empty');
        } else {
            $('#list').removeClass('empty');
        }
    });
});

body {
    margin: 0 auto;
    height: 100vh;
    box-sizing: border-box;
    padding: 50px;
    font: 16px "Lucida Grande", Helvetica, Arial, sans-serif;
    background: #f0f0f0;
}

.wrapper {
    height: 100%;
    display: flex;
    flex-direction: column;
}

.list-wrap label {
  float:left;
  color:#00BDE8;
}

.list-count {
  display: inline-block;
}

li {
  transition-property: margin, background-color, border-color;
  transition-duration: .4s, .2s, .2s;
  transition-timing-function: ease-in-out, ease, ease;
}

.empty-item {
  transition-property: opacity;
  transition-duration: 0s;
  transition-delay: 0s;
  transition-timing-function: ease;
}

.empty .empty-item {
  transition-property: opacity;
  transition-duration: .2s;
  transition-delay: .3s;
  transition-timing-function: ease;
}

.hiding {
  margin-left:-100%;
  opacity:0.5;
}

.hidden {
  display:none;
}

.form-container {
  box-sizing: border-box;
}

.list-container {
  box-sizing: border-box;
  position: relative;
  height: 100%;
}

.list-container input {
    display: inline-block;
}

ul {
