}

li:hover {
  background-color:#f9f9f9;
  border-color:#00BDE8;
}

.product-id {
    color: firebrick;
    font-weight: bold;
    display: block;
}
.product-department {
    display: block;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}
.product-title {
    display: block;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}
.product-price {
    display: block;
}
.product-discount {
    display: block;
}

.empty-item {
  background:#fff;
  color:#ddd;
  font-style:italic;
  border:none;
  text-align:center;
  visibility:hidden;
  opacity:0;
}

.empty .empty-item {
  opacity:1;
  visibility:visible;
}

