var index = context.get("deviceIndex")||0;
index = (index+1) % 20;
context.set("deviceIndex", index);

msg = {};
msg.device = "cart";
msg.deviceId = `Cart_${index}`;
return msg;

/* 
 * Workaround for class definition in node-red:
 * define the class inside a function with strict mode
 */
function makeGrid(mongoGrid) {
    "use strict";
    class Grid {
        constructor(mongoGrid) {
            this.grid = [];
            this.width = mongoGrid.width;
            this.height = mongoGrid.height;
            this.carts = {};
            for (var y = 0; y < this.height; y++) {
                this.grid[y] = [];
                for (var x = 0; x < this.width; x++) {
                    var cell = mongoGrid.grid[y][x];
                    this.grid[y][x] = cell === "" ? 0 : cell;
                }
            }
        }

        move(cartId, x, y) {
            this.remove(cartId);
            this.add(cartId,x,y);
        }

        add(cartId, x, y) {
            if (x < 0 || x >= this.width) {
                throw new Error(`out of grid x ${x}`);
            }
            if (y < 0 || y >= this.height) {
                throw new Error(`out of grid y ${y}`);
            }
            if (!Number.isInteger(this.grid[y][x])) {
                throw new Error(`invalid position ${x},${y}`);
            }
            this.grid[y][x] += 1;
            this.carts[cartId] = {x: x, y: y};
        }

        remove(cartId) {
            var oldPosition = this.carts[cartId];
            if (typeof oldPosition !== "undefined") {
                this.grid[oldPosition.y][oldPosition.x] -= 1;
            }
            this.carts[cartId] = undefined;
        }
    }

    return new Grid(mongoGrid);
}

var currentGridKey = "currentGrid";
var cumulatedGridKey = "cumulatedGrid";

var currentGrid = context.get(currentGridKey);
if (currentGrid === null || currentGrid === undefined) {
    currentGrid = makeGrid(msg.payload);
}

var cumulatedGrid = context.get(cumulatedGridKey);
if (cumulatedGrid === null || cumulatedGrid === undefined) {
    cumulatedGrid = makeGrid(msg.payload);
}

msg.value = JSON.parse(msg.value);
var x = Math.floor(msg.value.x);
var y = Math.floor(msg.value.y);
currentGrid.move(msg.deviceId, x, y);
cumulatedGrid.add(msg.deviceId, x, y);

context.set(currentGridKey, currentGrid);
context.set(cumulatedGridKey, cumulatedGrid);

msg.currentGrid = currentGrid;
msg.cumulatedGrid = cumulatedGrid;
return msg;

function isFreeCell(grid, width, height, x, y) {
    var X = Math.floor(x);
    var Y = Math.floor(y);
    return X >= 0 && X < width && Y >= 0 && Y < height && grid[Y][X] === "";
}

function getNewPosition(oldPosition, grid, width, height) {
    var maxTries = 0;
    var newPosition = {};
    do {
        var speed = Math.random() * (Math.random() > 0.4 ? 1.3 : 2.2);
        var angle = Math.random() * 2 * Math.PI;
        var xAngle = (5 * Math.cos(angle) + oldPosition.xAngle) / 6;
        var yAngle = (5 * Math.sin(angle) + oldPosition.yAngle) / 6;

        newPosition = {
            x : oldPosition.x + (speed * xAngle),
            y : oldPosition.y + (speed * yAngle),
            xAngle : xAngle,
            yAngle : yAngle
        };
        maxTries++;
    } while (maxTries < 3 &&
        !isFreeCell(grid, width, height, newPosition.x, newPosition.y));

    //rounding decimals
    newPosition.x = Number(Math.round(newPosition.x + 'e2') + 'e-2');
    newPosition.y = Number(Math.round(newPosition.y + 'e2') + 'e-2');
    return newPosition;
}

var indexKey = "deviceIndex";
var index = context.get(indexKey) || 0;
index = (index+1) % 20;
context.set(indexKey, index);

var width = msg.payload.width;
var height = msg.payload.height;
var grid = msg.payload.grid;

var positionKey = `position_${index}`;
var oldPosition = context.get(positionKey) || {
    x : Math.random() * width,
    y : Math.random() * height,
    xAngle : 0,
    yAngle : 0
};

var newPosition = getNewPosition(oldPosition, grid, width, height);

if (isFreeCell(grid, width, height, newPosition.x, newPosition.y)) {
    msg = {};
    msg.device = "cart";
    msg.deviceId = `Cart_${index}`;
    msg.value = `${newPosition.x},${newPosition.y}`;
    context.set(positionKey, newPosition);
    return msg;
}

msg.operation = "findOne"
msg.payload = [
    {}, {}
]
return msg;

msg.operation = "find.toArray"
msg.payload = [{}, {_id:false}];
return msg;

var id = parseInt(msg.payload.id);
var price = parseFloat(msg.payload.price);
msg.operation = "updateOne";
msg.payload = [
    {
        'index' : id
    }, {
       $set: {
           'price.discount': price
        } 
    }
]  
return msg;

