];
return msg;

var product = msg.payload;
var department = product['department']
msg.topic = 'clients/' + department;

var title = product['title'];
var newPrice = msg.updatedPrice.toFixed(2) + product['price']['currency'];
msg.payload = title + " -> " + newPrice;
return msg;

var cart = parseInt(msg.req.params.cart);
msg.wrongId = isNaN(cart) || cart < 0 || cart > 20;
